        try {
            const response = await fetch(" https://striveschool-api.herokuapp.com/api/comments/" + this.props.asin,{
                headers:{
                    "Authorization": "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MGRjNzMzNGIzNTgxNzAwMTVjMjI3NWIiLCJpYXQiOjE2Mjk5NzkzNzUsImV4cCI6MTYzMTE4ODk3NX0.8oPWQmDkjhZppxF1O_JjqR5vYAfRMCIn_4zUs7p9_OM"

                }
            })
            if(response.ok){
                const data = await response.json()
                console.log(data)
                this.setState({
                    comment:data
                })
            }
        } catch (error) {
            console.log(error)

        }

    }
    render(){

        return(
            <>
            <h2>Comments</h2>
            {
                this.state.comment.map(c =>{
                   return <ListGroup key={c._id}>
                        <ListGroup.Item>{c.comment}</ListGroup.Item>
                        <ListGroup.Item>{c.rate}</ListGroup.Item>
                    </ListGroup>
                }

                )
            }

   </>
        )

    }
}
export default CommentArea
